  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default ({components, ...props}) => <MDXTag name="wrapper"  components={components}><MDXTag name="h1" components={components} props={{"id":"hooks"}}>{`Hooks`}</MDXTag>
<MDXTag name="p" components={components}>{`React Pixi now support React hooks ðŸŒŸ`}</MDXTag>
<MDXTag name="h3" components={components} props={{"id":"useapp"}}>{`useApp`}</MDXTag>
<MDXTag name="p" components={components}>{`Access the `}<MDXTag name="inlineCode" components={components} parentName="p">{`PIXI.Application`}</MDXTag>{` in your component`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-jsx","metaString":""}}>{`import { useApp } from '@inlet/react-pixi'

function MyComponent () {
  const app = useApp()
  // app => PIXI.Application

  return (
    ...
  )
}
`}</MDXTag></MDXTag>
<MDXTag name="h3" components={components} props={{"id":"usetick"}}>{`useTick`}</MDXTag>
<MDXTag name="p" components={components}>{`Bind listener to the `}<MDXTag name="inlineCode" components={components} parentName="p">{`PIXI.Application`}</MDXTag>{` ticker event`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-jsx","metaString":""}}>{`import { useTick } from '@inlet/react-pixi'

function MyComponent () {
  useTick(delta => {
    // do something here
  })

  return (
    ...
  )
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Example:`}</MDXTag>
<iframe
  height={500}
  scrolling="no"
  title="useTick"
  src="//codepen.io/inlet/embed/EOMBer/?height=300&theme-id=33987&default-tab=result&embed-version=2"
  frameBorder="no"
  allowtransparency="true"
  allowFullScreen="true"
  style={{ width: '100%' }} /></MDXTag>
